package models

type ErrorObject struct {
	ErrorActual error
	ErrorCode string
	ErrorMessage string
}

type ResponseSwag struct {
	Code string      `json:"code" example:"00"`
	ID   string      `json:"response_id"  example:"00"`
	Desc string      `json:"message"  example:"Successfully init session and get answer session"`
	Data ResponseSwagInitSess `json:"data"`
}

type ResponseSwagInitSess struct {
	 AnswerSession 	string		`json:"answer_session" example:""`
     Pid  			string	 	`json:"pid" example:"19681"`
     PortAudio  	string 		`json:"port_audio" example:"51361"`
     PortVideo		string   	`json:"port_video" example:"56827"`
}

func NewErrorObject() *ErrorObject {
	return &ErrorObject{
		ErrorActual:   nil,
		ErrorCode: "GE",
		ErrorMessage: "General Error",
	}
}
